contracts/DappWorks.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract DappWorks is Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    IERC20 public usdt;
    Counters.Counter private _jobCounter;

    enum JobState { Open, InProgress, Completed, Disputed, Resolved }

    struct JobStruct {
        uint id;
        address owner;
        address freelancer;
        string jobTitle;
        string description;
        string tags;
        uint minBudget;
        uint maxBudget;
        uint finalBudget;
        string githubUrl;
        uint timestamp;
        JobState state;
        // set default -1 to completionPct
        uint8 completionPct; // Used for dispute resolution
        address[] bidders;
    }

    struct BidStruct {
        uint id;
        uint jId;
        address account;
        uint bidAmount;
    }

    uint public platformCharge = 5; // 5%

    mapping(uint => JobStruct) public jobListings;
    mapping(uint => BidStruct[]) private jobBidders;
    mapping(uint => bool) public jobListingExists;
    mapping(uint => mapping(address => bool)) public hasPlacedBid;

    event DisputeRaised(uint indexed jobId, address indexed initiator);
    event DisputeResolved(uint indexed jobId, uint8 completionPct);

    modifier onlyJobOwner(uint id) {
        require(jobListings[id].owner == msg.sender, "Unauthorized entity");
        _;
    }

    modifier onlyFreelancer(uint id) {
        require(jobListings[id].freelancer == msg.sender, "Only freelancer can perform this action");
        _;
    }

    modifier onlyJobParties(uint id) {
        require(
            jobListings[id].owner == msg.sender || jobListings[id].freelancer == msg.sender,
            "Only job parties can perform this action"
        );
        _;
    }

    constructor(address _usdtAddress) {
        usdt = IERC20(_usdtAddress);
    }

    function addJobListing(
        string memory jobTitle,
        string memory description,
        string memory tags,
        uint minBudget,
        uint maxBudget
    ) public {
        require(bytes(jobTitle).length > 0, "Please provide a job title");
        require(bytes(description).length > 0, "Please provide a description");
        require(bytes(tags).length > 0, "Please provide tags");
        require(minBudget > 0, "Min budget must be greater than 0");
        require(maxBudget >= minBudget, "Max budget must be greater than or equal to min budget");

        _jobCounter.increment();
        uint jobId = _jobCounter.current();

        JobStruct storage jobListing = jobListings[jobId];
        jobListing.id = jobId;
        jobListing.owner = msg.sender;
        jobListing.jobTitle = jobTitle;
        jobListing.description = description;
        jobListing.tags = tags;
        jobListing.minBudget = minBudget;
        jobListing.maxBudget = maxBudget;
        jobListing.state = JobState.Open;
        jobListing.timestamp = block.timestamp;
        jobListingExists[jobId] = true;
    }

    function updateJob(
        uint id,
        string memory jobTitle,
        string memory description,
        string memory tags,
        uint minBudget,
        uint maxBudget
    ) public onlyJobOwner(id) {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.Open, "This job has been taken");

        jobListings[id].jobTitle = jobTitle;
        jobListings[id].description = description;
        jobListings[id].tags = tags;
        jobListings[id].minBudget = minBudget;
        jobListings[id].maxBudget = maxBudget;
    }

    function deleteJob(uint id) public onlyJobOwner(id) {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.Open, "This job has been taken");
        delete jobListings[id];
        jobListingExists[id] = false;
    }

    function bidForJob(uint id, uint bidAmount) public {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].owner != msg.sender, "Forbidden action!");
        require(jobListings[id].state == JobState.Open, "This job has been taken");
        require(!hasPlacedBid[id][msg.sender], "You have placed a bid already");

        BidStruct memory bid;
        bid.id = jobBidders[id].length;
        bid.jId = id;
        bid.account = msg.sender;
        bid.bidAmount = bidAmount;
        hasPlacedBid[id][msg.sender] = true;

        jobListings[id].bidders.push(msg.sender);
        jobBidders[id].push(bid);
    }

    function acceptBid(uint id, address bidder) public onlyJobOwner(id) {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.Open, "This job has been taken");
        require(hasPlacedBid[id][bidder], "Unidentified bidder");

        uint bidAmount = 0;
        for (uint i = 0; i < jobBidders[id].length; i++) {
            if (jobBidders[id][i].account == bidder) {
                bidAmount = jobBidders[id][i].bidAmount;
                break;
            }
        }
        require(bidAmount > 0, "Bid not found");

        jobListings[id].freelancer = bidder;
        jobListings[id].finalBudget = bidAmount;
        jobListings[id].state = JobState.InProgress;

        usdt.transferFrom(msg.sender, address(this), bidAmount);
    }

    function completeJob(uint id, string memory githubUrl) public onlyFreelancer(id) {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.InProgress, "Job not in progress");

        jobListings[id].githubUrl = githubUrl;
        jobListings[id].state = JobState.Completed;
    }

    function payout(uint id) public nonReentrant onlyJobOwner(id) {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.Completed, "Job not completed or is disputed");

        uint finalBudget = jobListings[id].finalBudget;
        uint platformFee = (finalBudget * platformCharge) / 100;
        uint freelancerFee = finalBudget - platformFee;

        usdt.transfer(jobListings[id].freelancer, freelancerFee);
        usdt.transfer(owner(), platformFee);

        jobListings[id].state = JobState.Resolved;
    }

    function raiseDispute(uint id) public onlyJobParties(id) {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.Completed, "Job must be completed to raise a dispute");

        jobListings[id].state = JobState.Disputed;
        emit DisputeRaised(id, msg.sender);
    }

    function resolveDispute(uint id, uint8 pct) public nonReentrant onlyOwner {
        require(jobListingExists[id], "This job listing doesn't exist");
        require(jobListings[id].state == JobState.Disputed, "Job is not in dispute");
        require(pct <= 100, "Percentage must be between 0 and 100");

        jobListings[id].completionPct = pct;
        uint finalBudget = jobListings[id].finalBudget;

        uint platformFee = (finalBudget * platformCharge) / 100;
        uint remainingAfterFee = finalBudget - platformFee;

        uint freelancerShare = (remainingAfterFee * pct) / 100;
        uint clientShare = remainingAfterFee - freelancerShare;

        if (freelancerShare > 0) {
            usdt.transfer(jobListings[id].freelancer, freelancerShare);
        }
        if (clientShare > 0) {
            usdt.transfer(jobListings[id].owner, clientShare);
        }

        usdt.transfer(owner(), platformFee);

        jobListings[id].state = JobState.Resolved;
        emit DisputeResolved(id, pct);
    }

    function getBidders(uint id) public view returns (BidStruct[] memory) {
        return jobBidders[id];
    }

    function getJobs() public view returns (JobStruct[] memory) {
        uint count = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].state == JobState.Open) {
                count++;
            }
        }

        JobStruct[] memory activeJobs = new JobStruct[](count);
        uint currentIndex = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].state == JobState.Open) {
                activeJobs[currentIndex++] = jobListings[i];
            }
        }
        return activeJobs;
    }

    function getDisputedJobs() public view onlyOwner returns (JobStruct[] memory) {
        uint count = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].state == JobState.Disputed) {
                count++;
            }
        }

        JobStruct[] memory disputedJobs = new JobStruct[](count);
        uint currentIndex = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].state == JobState.Disputed) {
                disputedJobs[currentIndex++] = jobListings[i];
            }
        }
        return disputedJobs;
    }

    function getMyJobs() public view returns (JobStruct[] memory) {
        uint count = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].owner == msg.sender) {
                count++;
            }
        }

        JobStruct[] memory myJobs = new JobStruct[](count);
        uint currentIndex = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].owner == msg.sender) {
                myJobs[currentIndex++] = jobListings[i];
            }
        }
        return myJobs;
    }

    function getJob(uint id) public view returns (JobStruct memory) {
        return jobListings[id];
    }

    function getAssignedJobs() public view returns (JobStruct[] memory) {
        uint count = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].freelancer == msg.sender) {
                count++;
            }
        }

        JobStruct[] memory assignedJobs = new JobStruct[](count);
        uint currentIndex = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (jobListingExists[i] && jobListings[i].freelancer == msg.sender) {
                assignedJobs[currentIndex++] = jobListings[i];
            }
        }
        return assignedJobs;
    }

    function getJobsForBidder() public view returns (JobStruct[] memory) {
        uint count = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (hasPlacedBid[i][msg.sender]) {
                count++;
            }
        }

        JobStruct[] memory bidderJobs = new JobStruct[](count);
        uint currentIndex = 0;
        for (uint i = 1; i <= _jobCounter.current(); i++) {
            if (hasPlacedBid[i][msg.sender]) {
                bidderJobs[currentIndex++] = jobListings[i];
            }
        }
        return bidderJobs;
    }
}

contracts/USDT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract USDT is ERC20, Ownable {
    constructor() ERC20("Mock USDT", "USDT") {
        _mint(msg.sender, 1000000 * 10**decimals());
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function faucet() public {
        require(balanceOf(msg.sender) == 0, "You already have tokens.");
        _mint(msg.sender, 1000 * 10**decimals());
    }
}

